mapper.update:
UPDATE BOARD_POST SET
	FILE_ID=#{file_id},
	TITLE=#{title},
	CONTENT=#{content},
	EXP_YN=#{exp_yn}
WHERE BOARD_P_SEQ=#{board_p_seq}

UPDATE BOARD_POST SET
	EXP_YN=EXP_YN*-1
	WHERE BOARD_P_SEQ=#{board_p_seq}

UPDATE BOARD_POST SET
	BOARD_LEVEL=#{board_p_seq}
	WHERE BOARD_P_SEQ=#{board_p_seq}

UPDATE BOARD_POST SET
	BOARD_LEVEL=#{board_level}
	WHERE BOARD_P_SEQ=#{board_p_seq}

UPDATE BOARD_POST SET
	BOARD_COUNT=BOARD_COUNT+1
	WHERE BOARD_P_SEQ=#{board_p_seq}
